import React from "react";
import Card from "./components/Card";
import Filter from "./components/Filter";
import "./App.css"

export default function App()
{
    let [wholeData, setWholeData] = React.useState([]);
    
    const [data, setData] = React.useState([]);

    const [filters, setFilters] = React.useState([]);

    function addFilter(event)
    {
        const tag = event.target.innerHTML;

        if(filters.includes(tag))
        {
            setFilters((prevFilters) => [...prevFilters, tag])
        }

        setData((prevData) => {
            const arr = wholeData.slice();

            for(let i = 0; i < arr.length; i++)
            {
               if(arr[i].keywords.includes(tag) == false)
               {
                    arr.splice(i, 1);
                    console.log(1);
                    i--;
               }

            }
            return arr;
            }
        );
    }

    React.useEffect(() => {
        fetch("https://storage.googleapis.com/programiz-static/hiring/software/job-listing-page-challenge/data.json")
        .then((result) => result.json())
        .then((result) => {
            setWholeData((prevWholeData) => result);
            setData((prevResult) => result);
        });
    }, []);

    


    return(
        <>
            <Filter
                data={data}
                setData={setData}
                filters={filters}
                setFilters={setFilters}
                wholeData={wholeData}
            />
            {
                data.map(
                        (item) => 
                        <Card
                            key={item.posted_on}
                            content={item}
                            data={data}
                            setData={setData}
                            filters={filters}
                            setFilters={setFilters}
                            wholeData={wholeData}
                            addFilter={addFilter}
                        />
                    )
            }
        </>
    );
}
