    //                     arr.push(wholeData[i]);
    //                 }
    //             }
    //         }
    //     }
    //     if(arr.length == 0)
    //     {
    //         setData(wholeData);
    //     }
    //     else
    //     {
    //         setData(arr);
    //     }
    // }, [filters]);

    


    return(
        <>
            <Filter
                filters={filters}
                setFilters={setFilters}
            />
            {data.map(
                        (item) => 
                        <Card
                            key={item.posted_on}
                            content={item}
                            filters={filters}
                            setFilters={setFilters}
                        />
                    )
            }
        </>
    );
}
