export default function Card(props)
{
    function addFilter(event)
    {
        const tag = event.target.innerHTML;

        if(!props.filters.includes(tag))
        {
            props.setFilters((prevFilters) => [...prevFilters, tag]);
            console.log(props.filters.length);
        }

        props.setData((prevData) => {
            const arr = props.wholeData.slice();

            for(let i = 0; i < arr.length; i++)
            {
                for(let j = 0; j < props.filters.length; j++)
                {
                    if(arr[i].keywords.includes(props.filters[j]))
                    {
                        arr.splice(i, 1);
                        i--;
                        break;
                    }
                }

            }
            return arr;
            }
        );
    }

    return(
        <div className="card">

            <div>
                <img className="card-company-image" src={props.content.company_logo} />
            </div>

            <div className="card-main-content">
                <b className="card-company-name">
                    {props.content.company}
                </b>
                <h3 className="card-position">
                    {props.content.position}
                </h3>
                <div className="card-details">
                    <small>{props.content.posted_on}</small>
                    <small>{props.content.timing}</small>
                    <small>{props.content.location}</small>
                </div>
            </div>

            <div className="card-keywords">
                    {
                        props.content.keywords.map(
                            (item) => 
                            <li key={item} onClick={addFilter}>
                                {item}
                            </li>
                        )
                    }
            </div>

        </div>
    );
}
