import React from "react";
import Card from "./components/Card";
import Filter from "./components/Filter";
import "./App.css"

export default function App()
{
    let [wholeData, setWholeData] = React.useState([]);
    
    const [data, setData] = React.useState([]);

    const [filters, setFilters] = React.useState([]);

    React.useEffect(() => {
        fetch("https://storage.googleapis.com/programiz-static/hiring/software/job-listing-page-challenge/data.json")
        .then((result) => result.json())
        .then((result) => {
            setWholeData(result);
            setData(result);
        });
    }, []);

    React.useEffect(() => {
        if(filters.length !== 0)
        {
            const arr = [];

            const newFilter = filters[filters.length - 1];

            console.log("The new filter added is ", newFilter);

            for(let i = 0; i < wholeData.length; i++)
            {
                if(wholeData[i].keywords.includes(newFilter))
                {
                    if (!arr.includes(wholeData[i])) {
                        arr.push(wholeData[i]);
                    }
                }
            }
            if(arr.length == 0)
            {
                setData(wholeData);
            }
            else
            {
                setData(arr);
            }
        }
    }, [filters]);

    
    return(
        <>
            <Filter
                filters={filters}
                setFilters={setFilters}
            />
            {data.map(
                        (item) => 
                        <Card
                            key={item.posted_on}
                            content={item}
                            filters={filters}
                            setFilters={setFilters}
                        />
                    )
            }
        </>
    );
}
